create materialized view staking as
	with 
	ergopad as (
		select 'ergopad' as project_name
			, u.box_id
			, u.height
			, (u.assets->'d71693c49a84fbbecd4908c94813b46514b18b67a99952dc1e6e4791556de413')::bigint as amount
			, (u.assets->'1028de73d018f0c9a374b71555c5b8f1390994f2f41633e7b9d68f77735782ee')::int as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
		from utxos u
			join tokens t on t.token_id = 'd71693c49a84fbbecd4908c94813b46514b18b67a99952dc1e6e4791556de413'
		where ergo_tree = ''
	)
	, paideia as (
		select 'paideia' as project_name
			, u.box_id
			, u.height
			, (u.assets->'1fd6e032e8476c4aa54c18c1a308dce83940e8f4a28f576440513ed7326ad489')::bigint as amount
			, (u.assets->'245957934c20285ada547aa8f2c8e6f7637be86a1985b3e4c36e4e1ad8ce97ab')::int as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
		from utxos u
			join tokens t on t.token_id = '1fd6e032e8476c4aa54c18c1a308dce83940e8f4a28f576440513ed7326ad489'
		where ergo_tree = ''
	), egio as (
		select 'egio' as project_name
			, u.box_id
			, u.height
			, (u.assets->'00b1e236b60b95c2c6f8007a9d89bc460fc9e78f98b09faec9449007b40bccf3')::bigint as amount
			, (u.assets->'012d649686deeef606d253146bec0cf623f9b84574fbfa0fd0d1091393923613')::int as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
		from utxos u
			join tokens t on t.token_id = '00b1e236b60b95c2c6f8007a9d89bc460fc9e78f98b09faec9449007b40bccf3'
		-- egio (original)
		where ergo_tree = ''
	), egiov2 as (
		select 'egiov2' as project_name
			, u.box_id
			, u.height
			, (u.assets->'00b1e236b60b95c2c6f8007a9d89bc460fc9e78f98b09faec9449007b40bccf3')::bigint as amount
			, (u.assets->'012d649686deeef606d253146bec0cf623f9b84574fbfa0fd0d1091393923613')::int as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
		from utxos u
			join tokens t on t.token_id = '00b1e236b60b95c2c6f8007a9d89bc460fc9e78f98b09faec9449007b40bccf3'
		-- egiov2
        where ergo_tree = ''
	), neta as (
		select 'neta' as project_name
			, u.box_id
			, u.height
			, (u.assets->'472c3d4ecaa08fb7392ff041ee2e6af75f4a558810a74b28600549d5392810e8')::bigint as amount
			, (u.assets->'a94787d05eefbd1b773b62812123b91c40553bd5ed7f3092ae66dba3f812e0c0')::bigint as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
			, u.assets
		from utxos u
			join tokens t on t.token_id = '472c3d4ecaa08fb7392ff041ee2e6af75f4a558810a74b28600549d5392810e8'
		-- neta
        where ergo_tree = ''
	), aht as (
		select 'aht' as project_name
			, u.box_id
			, u.height
			, (u.assets->'18c938e1924fc3eadc266e75ec02d81fe73b56e4e9f4e268dffffcb30387c42d')::bigint as amount
			, (u.assets->'6c6e6ec794585aeeac8d9a211a270caec3d0fce9c352f8df2cc54b2c32217648')::bigint as proxy
			, u.registers->'R4' as penalty
			, regexp_replace(u.registers->'R5', '^0e20', '') as stakekey_token_id
			, t.decimals
			, t.token_id
			, u.assets
		from utxos u
			join tokens t on t.token_id = '18c938e1924fc3eadc266e75ec02d81fe73b56e4e9f4e268dffffcb30387c42d'
		-- neta
        where ergo_tree = ''
	)
	, a as (
		select 
			address
			, id
			, (each(assets)).key::varchar(64) as token_id
			, (each(assets)).value::bigint as amount
		from utxos
		-- where address = '9hMa8bqVq31HYaCqbufEbWZFURFrHhxuHui3w1dmAtFCF1r4LZm'
	)
	-- ergopad
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from ergopad u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

	-- paideia
	union all
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from paideia u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

    -- egio
	union all
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from egio u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

    -- egiov2
	union all
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from egiov2 u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

    -- neta
	union all
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from neta u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

	-- aht
	union all
	select
		a.address
		, u.token_id
		, u.box_id
		, u.stakekey_token_id
		, u.amount/power(10, u.decimals) as amount
		, u.penalty
        , u.project_name
	from aht u
		join a on a.token_id = u.stakekey_token_id
	where proxy = 1

    with no data;

create unique index uq_staking on staking (box_id, token_id);
